heaxagonLayer = {
  const hexagonLayer = new deck.HexagonLayer({
    id: 'heatmap',
    colorRange,
    data: data,
    elevationRange: [0, 1000],
    elevationScale: data && data.length ? 20 : 0,
    extruded: true,
    getPosition: d => [Number(d.longitude), Number(d.latitude)],
    opacity: .1,
    radius: 100,
    coverage: 0.9,
    upperPercentile: 95,
    lightSettings,
    pickable: true,
    autoHighlight: true,
    onHover: onHover,
    onClick: onClick,
    transitions: {
      elevationScale: 1000
    },
    material: {
      ambient: 0.64,
      diffuse: 0.6,
      shininess: 32,
      specularColor: [51, 51, 51]
    }
  });

  deckgl.setProps({layers: [hexagonLayer]});
  return hexagonLayer;
}

