function onHover (info) {
  const data = info.object;
  if (data) {
    tooltip.style.left = `${info.x}px`;
    tooltip.style.top = `${info.y}px`;
    tooltip.innerHTML = `<b>${data.cellWeight.toLocaleString()} trips to/from this location</b>`;
  } else { 
    tooltip.innerHTML = '';
  }
}

